import { __decorate } from "tslib";
import { Component, Input } from '@angular/core';
import { Menu_Icon } from "./dataIcons";
// import * as fs from "file-system";
let ShowProjectComponent = class ShowProjectComponent {
    constructor(serverHttp, httpClient) {
        this.serverHttp = serverHttp;
        this.httpClient = httpClient;
        this.menuType = Menu_Icon;
        this.listFolder = [];
        this.listParent = [];
        this.listData = [];
        this.listIcon = [];
    }
    ngOnInit() {
        this.serverHttp.getFolder().subscribe((data) => {
            if (data) {
                this.listFolder = data;
                this.listParent[0] = this.listFolder[0];
            }
        });
        this.serverHttp.getFile().subscribe((data) => {
            if (data) {
                this.listFile = data;
            }
            this.getData();
        });
        this.serverHttp.getIcons().subscribe((data) => {
            this.listIcon = data;
        });
    }
    async getData() {
        let parentId = this.listParent[this.listParent.length - 1].id;
        this.listData = this.listFolder.filter(item => item.parentId == parentId).concat(this.listFile.filter(item => item.parentId == parentId));
    }
    moveTo(item) {
        if (this.listParent.includes(item)) {
            this.listParent.splice(this.listParent.indexOf(item), this.listParent.length);
        }
        else {
            this.listParent.push(item);
        }
        console.log(this.listParent);
        this.getData();
    }
    checkType(name) {
        let checkName = name.split('.');
        let type = checkName[checkName.length - 1];
        if (this.menuType[0].typeOfImg.includes(type)) {
            return 1;
        }
        else if (this.menuType[0].typeOfText.includes(type)) {
            return 0;
        }
        else {
            return 2;
        }
        console.log(type);
    }
